mapper.select:
select t.id id, -- 主键 t.type type, -- 类型（1- 通知 2-汇报） t.user_id userId, -- 用户id（发送者） t.receive_user_id receiveUserId, -- 接收者id t.context context, -- 消息内容 t.files files, -- 附件 t.status status, -- 是否读（0-未确认 1-出错 2-已确认） t.create_time createTime, -- 创建时间 t.update_time updateTime -- 更新时间 from message t
and t.type = #{p.type}
and t.user_id = #{p.userId}
and t.receive_user_id = #{p.receiveUserId}
and t.context LIKE '%${p.context}%'
and t.files LIKE '%${p.files}%'
and t.status = #{p.status}
and t.create_time >= #{p.startTime}
and #{p.endTime} >= t.create_time
order by t.id desc
